mapper.select:
select blocker_id select *
from Block from Block
where blocking_id = #{blocking_id} where blocker_id = #{blocker_id}

select blocking_id select *
from Block from Block
where blocker_id = #{blocker_id} where blocking_id = #{blocking_id}

mapper.delete:
delete from Block
where blocking_id = #{blocking_id} and
blocker_id = #{blocker_id}
